import React from "react";
import myImage from "../img/myImage.png";

class About extends React.Component {
    constructor() {
        super();
        this.state = {
            skills: [
                {
                    id: "HTML5_skill",
                    content: "HTML5",
                    porcentage: "80%",
                    value: "80"
                },
                {id: "CSS3_skill",
                  content: "CSS3",
                  porcentage: "75%",
                  value: "75"},
                {
                    id: "JavaScript_skill",
                    content: "JavaScript",
                    porcentage: "90%",
                    value: "90"
                },
                {id: "PHP_skill",
                  content: "PHP",
                  porcentage: "70%",
                  value: "70"},
                {
                    id: "ReactJS_skill",
                    content: "ReactJS",
                    porcentage: "40%",
                    value: "40"
                },
                {
                    id: "Jquery_skill",
                    content: "Jquery",
                    porcentage: "90%",
                    value: "90  "
                },
                {
                    id: "VanillaJS_skill",
                    content: "VanillaJS",
                    porcentage: "85%",
                    value: "85"
                },
                {
                    id: "Laravel_skill",
                    content: "Laravel",
                    porcentage: "70%",
                    value: "70"
                }
            ],
            about_me: [
                {
                    id: "first-p-about",
                    content:
                        'My name is Pahrefly Fattah Affandi. My motto is "JUST DO IT ! later". No, kidding. My motto is "JUST DO IT FUCKING RIGHT NOW" because i have a BIG DREAM.'
                },
                {
                    id: "second-p-about",
                    content:
                       "Imma be Fullstack Developer but damn my brain are suck. Now im Fullstack Overflow :D"
                },
            ]
        };
    }

    render() {
        return (
            <section id="about" className="about-mf sect-pt4 route">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="box-shadow-full">
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="row">
                                            <div
                                                className="col-sm-6 col-md-5"
                                                style={{margin: "0 auto"}}
                                            >
                                                <div
                                                    className="about-img"
                                                    style={{textAlign: "center"}}
                                                >
                                                    <img
                                                        className="img-fluid rounded b-shadow-a"
                                                        alt=""
                                                    />
                                                </div>
                                            </div>
                                        </div>
                                        <div className="skill-mf">
                                            {/* <p className="title-s">Skill</p> */}
                                            {this.state.skills.map(skill => {
                                                return (
                                                    <React.Fragment key={skill.id}>
                                                        <span>{skill.content}</span>{" "}
                                                        <span className="pull-right">
                              {skill.porcentage}
                            </span>
                                                        <div className="progress">
                                                            <div
                                                                className="progress-bar"
                                                                role="progressbar"
                                                                style={{width: skill.porcentage}}
                                                                aria-valuenow={skill.value}
                                                                aria-valuemin="0"
                                                                aria-valuemax="100"
                                                            ></div>
                                                        </div>
                                                    </React.Fragment>
                                                );
                                            })}
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="about-me pt-4 pt-md-0">
                                            <div className="title-box-2">
                                                <h5 className="title-left">About Me</h5>
                                            </div>
                                            {this.state.about_me.map(content => {
                                                return (
                                                    <p className="lead"  key={content.id}>
                                                        {content.content}
                                                    </p>
                                                );
                                            })}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default About;
